import React,{Component} from 'react';
import classes from './SignInBody_n.module.css';
import axios from '../../axios'
import {Redirect, withRouter} from 'react-router-dom'
class Role extends Component{
	//0 : M, 1 : C, 2 : SA

	state={
		options :[0,1,2],
		loading:true
	}

	onSubmitHandler = (event) =>{
		// event.preventDefault();
  //       const formData = {};

  //       const loginInfo = {
  //           userID : this.state.userID,
  //           password: this.state.password,
  //       }

  //       axios.post( '/login.json', loginInfo )
  //           .then( response => {

  //      			this.props.history.push('/login/choose-role/');

  //           } )
  //           .catch( error => {
  //               console.log("Error", error);
  //           });
	}

	componentDidMount(){
		//get roles from the server.
	}

	render(){
		//console.log('[SignInBody]',this.props);
		const roles = this.state.options? this.state.options.map(role=>{
			let roleName = null;
			if(role==0)
				roleName='Manager'
			else if(role==1)
				roleName='Canvasser'
			else
				roleName='System Admin'

			let r = <div className={classes.RoleItem}><input type='radio' value = {role} name='role'/>{roleName}</div>
			return r
		}) : null;

		return (
			<div className={[classes.SignInBody, "container", "text-center"].join(' ')}> 
				<h1 className = {classes.Title}>Select Role</h1>

				<div className = {classes.RoleSection} onChange={this.setGender.bind(this)}>
					{roles}
				</div>

			</div>

		);
	}
}

export default withRouter(Role);
