import React, {Component} from 'react'
import TaskAssignmentListHead from './TaskAssignmentListHead'
import TaskAssignmentItem from './TaskAssignmentItem'
import axios from '../../../axios'
class TaskAssignmentList extends Component{


	state = {
		tasks : null,
		load : false,
	}

	componentDidUpdate(){
		if(this.props.campaign){
			console.log(['TaskAssignmentList'], this.props.campaign.tasks);
			if(this.props.campaign.tasks){

				//get Tasks from server
				axios.get('https://cse308-de3df.firebaseio.com/tasks')
			}
		}
	}


	render(){



		let tasks =  this.state.tasks? this.state.tasks.map(tsk =>{
			let task = <TaskAssignmentItem /> 
		    return task;
		  }) : <h1>No Task</h1>;

		const comp = this.props.campaign? (<div>
			<div className='col-11'>
				<TaskAssignmentListHead />
				{tasks}
			</div>

		</div>): null;
		return (
			<div>
				{comp}
			</div>
		) ;
	}
}

export default TaskAssignmentList;
