import React, {Component} from 'react'
import classes from './Availability.module.css'


class Canvasser extends Component{
	state = {...this.props,
		selectedCanvasser : null}


	selectCanvasser = (canv) =>{
		console.log('selected', canv);
		for(let i=0; i<this.state.canvassers.length; i++){
			if(this.state.canvassers[i]==canv){
				this.setState({selectedCanvasser : canv});
				break;
			}
		}
	}

	render(){
		return (
			<div className = {['row', classes.Section].join(' ')}>
				<div className = {['col-3'].join(' ')}>
					<h2> Canvasser</h2>
				</div>
				<div className = {['col-6'].join(' ')}>
					<h2>{this.props.date}</h2>
				</div>
				<div className = {['col-3'].join(' ')}>
					<button className = 'btn btn-light' onClick={this.props.modalOpen}>Search</button>
				</div>

			</div>
		);

	}
}

export default Canvasser
