import React, {Component} from 'react'
import Dropdown from 'react-dropdown'
import classes from './TaskAssignment.module.css'
import './Dropdown.css'
class TaskAssignmentHead extends Component{
	state = {
		selectedCampaign : null,
		campaignTitles : null,

	}

	getTitles = () =>{
		if(this.props.campaigns && !this.state.campaignTitles){
			let campaignList = []
			for(let i = 0; i<this.props.campaigns.length; i++){
				let campaign = {value : this.props.campaigns[i].campaignTitle, key: i}
		 		campaignList.push(campaign);
			 }

			 this.setState({campaignTitles : campaignList});
		}
	}

	dropDownHandler = (event) =>{
		console.log('[dropdownHandler]',event.key)
		//this.setState({selectedCampaign:event.value})
	}

	render(){
		//const campaignTitles = 
		this.getTitles();

		const dropdown = this.state.campaignTitles?<Dropdown 
								 className = {[classes.Dropdown].join(' ')}
								 placeholderClassName ="dropdown-toggle"
							     options = {this.state.campaignTitles}
							     placeholder="Choose a Campaigin"
							     value = {this.state.selectedCampaign?this.state.selectedCampaign : 'Choose a Campaigin'}
							     onChange={this.dropDownHandler}/> : null;


		return  <div className={['row'].join(' ')}>
					<div className={['col-4'].join(' ')}>
						<h4 className={[].join(' ')}>Select Campaign</h4>
					</div>
					<div className={['col-6'].join(' ')}>
						<div className={['row'].join(' ')}>
							{dropdown}
						</div>
					</div>
				</div>
	}
}

export default TaskAssignmentHead;
