import React, {Component} from 'react'
import Dropdown from 'react-dropdown'

class TaskAssignmentHead extends Component{

	render(){
		return  <div className={['row'].join(' ')}>
					<div className={['col-4'].join(' ')}>
						<h4 className={[].join(' ')}>Select Campaign</h4>
					</div>
					<div className={['col-6'].join(' ')}>
						<div className={['row'].join(' ')}>
							<Dropdown 
								 placeholderClassName ="dropdown-toggle"
								 className = {[classes.Dropdown].join(' ')}
							     options = {this.state.options}
							     placeholder="Role"
							     value={this.state.role?this.state.role:'Role'}
								 onChange={(event) => this.setState({role:event.value})}/>
						</div>
					</div>
				</div>
	}
}

export default TaskAssignmentHead;
