import React, {Component} from 'react'
import classes from './TaskDetail.module.css'
import { Map, Marker, Popup, TileLayer } from 'react-leaflet'
import './map.css'

import LocationItem from './LocationItem'

class Locations extends Component{


	render(){
		console.log(this.props.locations)
		const position = [51.505, -0.09]

		const locations = this.props.locations&&this.props.locations.length>0? this.props.locations.map(loc =>{
			let location = <LocationItem key= {loc.id} location={loc.location}/> 
			console.log("hey", loc.location);
		    return location;
		  }): null;

		return <div>
				<h6>Locations</h6>
					<div id="map" className = {classes.MapZone}>
						<Map center={position} zoom={13}>
							<TileLayer
						          attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
						          url='http://{s}.tile.osm.org/{z}/{x}/{y}.png'/>
						    <Marker position={position}>
						      <Popup>A pretty CSS3 popup.<br />Easily customizable.</Popup>
						    </Marker>
					  	</Map>		

				</div>
				<div>
					<h6>List</h6>
					<div>
						{locations}
					</div>
				</div>

			</div>;
	}
}

export default Locations;
