import React, {Component} from 'react'
import classes from './Availability.module.css'

class SearchCanvasser extends Component{
	state = {
		...this.props,
		canvassers : [],
	}

	componentDidMount(){
		let cmpIndex = this.props.match.params.id;
     	axios.get('https://cse308-de3df.firebaseio.com/campaigns/'+cmpIndex+'.json').then(response=>{
          let x= response.data
          let newCampaign = x;
          this.setState({canvassers:newCampaign});
      });     
	}

	render(){
		return (
			<div className = {['row'].join(' ')}>
				<div className = {['col-3'].join(' ')}>
					<h2> Task Date</h2>
				</div>
				<div className = {['col-6', classes.TaskDate].join(' ')}>
					<h2>{this.props.date}</h2>
				</div>
			</div>
		);
	}
}

export default SearchCanvasser
